document.addEventListener('DOMContentLoaded', function() {
    const title = document.getElementById('title');
    let isBlue = true;

    // toggle title color from blue to green every second
    setInterval(() => {
        title.style.color = isBlue ? 'green' : 'blue';
        isBlue = !isBlue;
    }, 1000);
});

/* Reset and base styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Container styles */
.container {
    background: white;
    padding: 2rem;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    text-align: center;
    max-width: 400px;
    width: 90%;
}

/* Title styles */
#title {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: #2c3e50;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
    transition: color 0.3s ease;
}

#title:hover {
    transform: scale(1.05);
    transition: transform 0.3s ease;
}

/* Button styles */
#colorBtn {
    background: linear-gradient(45deg, #ff6b6b, #ee5a24);
    color: white;
    border: none;
    padding: 12px 24px;
    font-size: 1.1rem;
    border-radius: 25px;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(238, 90, 36, 0.3);
}

#colorBtn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(238, 90, 36, 0.4);
}

#colorBtn:active {
    transform: translateY(0);
}

/* Responsive design */
@media (max-width: 600px) {
    .container {
        padding: 1.5rem;
    }

    #title {
        font-size: 2rem;
    }

    #colorBtn {
        padding: 10px 20px;
        font-size: 1rem;
    }
}

/* Additional animations */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.container {
    animation: fadeIn 0.6s ease-out;
}

/* Extra utility classes */
.text-center {
    text-align: center;
}

.margin-top {
    margin-top: 1rem;
}

.margin-bottom {
    margin-bottom: 1rem;
}

.padding-small {
    padding: 0.5rem;
}

.padding-medium {
    padding: 1rem;
}

.padding-large {
    padding: 2rem;
}
